    public enum LightQuestType
    {
        Hunt = 1,
        Delivery = 2
    }

    public class LightQuestInfo(
        uint questId, 
        QuestAreaId areaId, 
        LightQuestType type, 
        QuestBoardBaseId board,
        string name
    )
    {
        public QuestId QuestId { get; private set; } = (QuestId)questId;
        public QuestAreaId AreaId { get; private set; } = areaId;
        public LightQuestType Type { get; private set; } = type;
        public QuestBoardBaseId Board { get; private set; } = board;
        public string Name { get; private set; } = name;
        public bool IsAreaOrder { get; private set; } = name.Contains("Area Order");
    }

    public class LightQuestId
    {
        private static readonly Dictionary<QuestBoardBaseId, HashSet<LightQuestInfo>> BoardIdToLightQuestInfoMap = Enum.GetValues<QuestBoardBaseId>().ToDictionary(x => x, x => new HashSet<LightQuestInfo>());
        private static readonly Dictionary<QuestAreaId, HashSet<LightQuestInfo>> QuestAreaIdToLightQuestInfoMap = Enum.GetValues<QuestAreaId>().ToDictionary(x => x, x => new HashSet<LightQuestInfo>());
        private static readonly Dictionary<QuestId, LightQuestInfo> QuestIdToLightQuestInfoMap = [];

        static LightQuestId()
        {
            Type myType = typeof(LightQuestId);
            FieldInfo[] staticFields = myType.GetFields(BindingFlags.Static | BindingFlags.Public);

            foreach (FieldInfo field in staticFields)
            {
                LightQuestInfo value = (LightQuestInfo)field.GetValue(null);

                BoardIdToLightQuestInfoMap[value.Board].Add(value);
                QuestAreaIdToLightQuestInfoMap[value.AreaId].Add(value);
                QuestIdToLightQuestInfoMap[value.QuestId] = value;
            }
        }

        public static LightQuestInfo FromQuestId(QuestId id)
        {
            return QuestIdToLightQuestInfoMap[id];
        }

        public static LightQuestInfo FromQuestId(uint id)
        {
            return QuestIdToLightQuestInfoMap[(QuestId)id];
        }

        public static HashSet<LightQuestInfo> FromBoardId(QuestBoardBaseId id)
        {
            return BoardIdToLightQuestInfoMap.GetValueOrDefault(id, []);
        }

        public static HashSet<LightQuestInfo> FromBoardId(QuestBoardBaseId id, LightQuestType type)
        {
            return BoardIdToLightQuestInfoMap.GetValueOrDefault(id, []).Where(x => x.Type == type).ToHashSet();
        }

        public static HashSet<LightQuestInfo> FromQuestAreaId(QuestAreaId id)
        {
            return QuestAreaIdToLightQuestInfoMap.GetValueOrDefault(id, []);
        }

